INSERT INTO `book` VALUES (1, '罗生门', 28, '芥川龙之介', '《罗生门》日本文坛的“鬼才”、“短篇小说之神”芥川龙之介擅长描写人物性格与玄妙的心理转折，笔锋犀利简洁，常一针见血藉作品暗讽社会丑恶现象。受家庭教养影响，芥川龙之介广泛且大量涉猎中国古代文学与世界经典，并常以此做为创作灵感，再加以独树一格的冷峻文笔，寄予独特的人生感怀，直打人性底层黑暗。好友菊池宽曾叹：“像他那样高身的教养，优秀的趣味，已及兼备和汉洋学问的作家，今后恐怕绝无仅有。”', 1000);
INSERT INTO `book` VALUES (2, '如父如子', 31, '是枝裕和', '当意识到孩子也在注视着自己时，那一瞬间，便懂得了什么是如父如子。 ', 1000);
